sp_widget.client_script:
api.controller=function($rootScope,$scope) {
  /* widget controller */
  var c = this;
  c.selectItem = function(idx) {
    var id = c.data.notes[idx].sys_id;
    console.log('Note ID: ' + id);
    $rootScope.noteID = id;
    $rootScope.$emit('selectNote', id);
  };
		$rootScope.$on('updateNote', function(event,data) {
		console.log('data action = '+data.updateType);
		c.server.get({
			action: data.updateType == 'title' ? 'getNoteTitle' : 'getNoteBody',
			noteID: data.noteID
		}).then(function(r) {
			console.log('result = '+r.data.note);

			c.data.noteID = r.data.note.sys_id;
			console.log("sys_id = "+c.data.noteID);

			var index = 0;
			for(var i=0;i<3;++i)
				{
					if(c.data.notes[i].sys_id==r.data.note.sys_id)
					{
						index = i;break;
					}
				}
			console.log("index = "+index);

			console.log("data action = "+r.data.note.action);
			if(r.data.note.action == 'title')
				{
					console.log("updating title");
					c.data.notes[index].title = r.data.note.title;
				}
			else if(r.data.note.action == 'body')
				{
					console.log("updating body");
					c.data.notes[index].note = r.data.note.note;
				}
		});
	});
};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	if(input && input.noteID)
	{
		var note = new GlideRecord('x_snc_createnotes_note');
		if (note.get(input.noteID)) 
		{
			if(input.action=='getNoteTitle')
			{
				data.note = {};
				data.note.action = 'title';
				$sp.getRecordValues(data.note, note, "title, sys_id");
			}
			else if(input.action == 'getNoteBody')
			{
				data.note = {};
				data.note.action = 'body';
				$sp.getRecordValues(data.note, note, "note, sys_id");
			}
		}

	}
	else
		(function() {
			//create an array to populate with notes
			data.notes = [];
			var noteGR = new GlideRecord('x_snc_createnotes_note');
			noteGR.addQuery('user', gs.getUser().getID());
			noteGR.orderByDesc('sys_created_on');
			noteGR.query();
			while (noteGR.next()) {
				var noteObj = {};
				//use service portal helper method to get some display values
				$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
				//get the first 20 characters of the description
				noteObj.note = noteGR.getValue('note').slice(0,20);
				//push the populated obj into the array
				data.notes.push(noteObj);
			}
		})();

})();
